use crate::application::use_cases::configuration::get_active_colorscheme::use_case_configuration_get_active_colorscheme;

use super::body::presentation_web_section_body;
use axum::response::Html;

pub async fn presentation_web_section_page() -> Html<String> {
    Html(
        r##"
    <!doctype html>
    <html lang="en">
        <head>
        <meta charset="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <meta http-equiv="X-UA-Compatible" content="ie=edge" />
        <link rel="icon" href="/preto_no_branco.ico" type="image/x-icon">
        <title>Lince</title>
        "##
        .to_string()
            + style().await.as_str()
            + htmx()
            + datastar()
            + "</head>"
            + presentation_web_section_body().await.as_str()
            + "</html>",
    )
}

pub async fn style() -> String {
    "<style>
        :root {"
        .to_string()
        + use_case_configuration_get_active_colorscheme().await
        + "}

        body {
            background-color: var(--background-color);
            color: var(--text-normal);
            margin: 0px;
            padding: 0px;
        }

        main {
            display: flex;
            flex-direction: row;
            gap: 1rem;
            flex-wrap: wrap;
        }

        table, th, td {
            border-collapse: collapse;
            padding: 0.25rem;
        }
        table {
            border: 0px solid var(--table-border);
            border-radius: var(--table-border-radius);
        }
        th, td {
            border: var(--table-cell-border-width) solid var(--table-border);
        }

        th {
            background-color: var(--table-th-bg);
        }
        th:hover {
            background-color: var(--table-th-bg-hover);
        }
        td {
            background-color: var(--table-td-bg);
        }
        td:hover {
            background-color: var(--table-td-bg-hover);
        }

        input {
            all: unset;
            color: var(--input-txt);
            background: var(--input-bg);
            border: 1px solid var(--input-border-color);
            padding: 0.25rem;
            border-radius: 2px;
        }

        input:focus {
            outline: none;
            box-shadow: 0 0 5px 2px var(--input-focus-shadow);
        }
        .active {
            background-color: var(--active-button-bg);
            color: var(--active-button-txt);
            border: 1px solid var(--active-button-border);
            border-radius: 2px;
        }
        .active:hover {
            background-color: var(--active-button-bg-hover);
            color: var(--active-button-txt);
            border: 1px solid var(--active-button-border-hover);
            border-radius: 2px;
        }

        .inactive {
            background-color: var(--inactive-button-bg);
            color: var(--inactive-button-txt);
            border: 1px solid var(--inactive-button-border);
            border-radius: 2px;
        }
        .inactive:hover {
            background-color: var(--inactive-button-bg-hover);
            color: var(--inactive-button-txt);
            border: 1px solid var(--inactive-button-border-hover);
            border-radius: 2px;
        }

        .filled {
            background: var(--background-color);
        }

        .middle_y {
            align-items: center;
        }

        .modal {
            position: fixed;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            z-index: 1;
        }

        .shy {
            width: min-content;
            height: min-content;
        }

        .row {
            display: flex;
            flex-direction: row;
            gap: 0.25rem;
        }
        .column {
            display: flex;
            flex-direction: column;
            gap: 0.1rem;
        }
    </style>"
}

